(340, 'grace', 'a:129:{s:13:"suxingme_logo";s:67:"http://192.168.1.35/wp-content/uploads/2017/07/2017070418330435.png";s:19:"suxingme_login_logo";s:67:"http://192.168.1.35/wp-content/uploads/2017/07/2017070418330435.png";s:22:"suxingme_login_bgp_num";s:1:"3";s:20:"suxingme_login_bgp_1";s:68:"http://192.168.1.35/wp-content/uploads/2017/07/2017070517443813.jpeg";s:20:"suxingme_login_bgp_2";s:0:"";s:20:"suxingme_login_bgp_3";s:0:"";s:20:"suxingme_login_bgp_4";s:0:"";s:20:"suxingme_login_bgp_5";s:0:"";s:20:"suxingme_login_bgp_6";s:0:"";s:19:"suxingme_author_bgp";s:0:"";s:16:"suxingme_favicon";s:0:"";s:16:"suxingme_p_n_bgp";s:67:"http://192.168.1.35/wp-content/uploads/2017/07/2017070517450736.jpg";s:24:"suxingme_statistics_code";s:0:"";s:14:"suxingme_beian";s:0:"";s:16:"suxingme_dis_seo";s:1:"1";s:9:"page_sign";s:1:"-";s:20:"suxingme_description";s:476:"Metaverse is a decentralized world that redefines digital identities and smart assets through\r\n                                blockchain technology：Within the peer to peer network resides universally unique and self-sovereign\r\n                                identities, attached are user-defined digital identity attributes and smart assets, which\r\n                                will essentially power all decentralized applications or smart contracts coming afterwards.";s:17:"suxingme_keywords";s:32:"Metaverse  blockchain technology";s:20:"suxingme_breadcrumbs";s:1:"1";s:21:"suxingme_autonofollow";s:1:"1";s:20:"suxingme_keywordlink";s:1:"1";s:8:"friendly";s:1:"1";s:19:"suxingme_head_fixed";s:1:"1";s:19:"suxingme_ajax_posts";s:1:"1";s:17:"suxingme_timthumb";b:0;s:23:"suxingme_post_thumbnail";s:0:"";s:26:"suxingme_timthumb_lazyload";s:1:"1";s:17:"default_thumbnail";s:0:"";s:21:"default_thumbnail_700";s:0:"";s:17:"suxingme_fancybox";b:0;s:18:"suxingme_post_tags";s:1:"1";s:14:"new_avatar_pic";s:61:"http://192.168.1.35/wp-content/themes/grace7.0/img/avatar.png";s:19:"suxingme_get_avatar";s:3:"two";s:20:"suxingme_text_in